mapper.select:
SELECT p.PRODUCT_NO, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_CREATE_DATE, PRODUCT_UPDATE_DATE, PRODUCT_FL, p.CATEGORY_NO "CATEGORY_NO", PARTNER_NO, UPLOAD_IMG_NO, UPLOAD_IMG_PATH, UPLOAD_IMG_OG_NAME, UPLOAD_IMG_RENAME, BIG_CATEGORY_NO, UPLOAD_IMG_ORDER, CATEGORY FROM PRODUCT p JOIN UPLOAD_FILE uf ON(p.PRODUCT_NO = uf.PRODUCT_NO) LEFT JOIN CATEGORY c ON(p.CATEGORY_NO = c.CATEGORY_NO) WHERE UPLOAD_IMG_ORDER = 0 AND PRODUCT_FL = 'N' AND CATEGORY = #{smallcategoryNo} AND PRODUCT_UPDATE_DATE IS NOT NULL
AND PRODUCT_PRICE > #{minPrice} AND PRODUCT_PRICE < #{maxPrice}
ORDER BY p.PRODUCT_NO

SELECT p.PRODUCT_NO, p.PRODUCT_NAME, p.PRODUCT_PRICE, p.PRODUCT_CREATE_DATE, p.PRODUCT_UPDATE_DATE, p.PRODUCT_FL, p.CATEGORY_NO, p.PARTNER_NO, uf.UPLOAD_IMG_NO, uf.UPLOAD_IMG_PATH, uf.UPLOAD_IMG_OG_NAME, uf.UPLOAD_IMG_RENAME, BIG_CATEGORY_NO, uf.UPLOAD_IMG_ORDER, CASE WHEN EXISTS (SELECT 1 FROM WISHLIST w WHERE w.PRODUCT_NO = p.PRODUCT_NO AND w.MEMBER_NO = #{memberNo}) THEN 1 ELSE 0 END AS WISH FROM PRODUCT p JOIN UPLOAD_FILE uf ON p.PRODUCT_NO = uf.PRODUCT_NO LEFT JOIN CATEGORY c ON p.CATEGORY_NO = c.CATEGORY_NO NATURAL JOIN BIG_CATEGORY WHERE uf.UPLOAD_IMG_ORDER = 0 AND p.PRODUCT_FL = 'N' AND c.CATEGORY_NO = #{smallcategoryNo} AND PRODUCT_UPDATE_DATE IS NOT NULL
AND PRODUCT_PRICE > #{minPrice} AND PRODUCT_PRICE < #{maxPrice}
ORDER BY CASE WHEN #{sort} = 1 THEN p.PRODUCT_UPDATE_DATE ELSE NULL END DESC, CASE WHEN #{sort} = 2 THEN p.PRODUCT_UPDATE_DATE ELSE NULL END ASC, CASE WHEN #{sort} = 3 THEN PRODUCT_PRICE ELSE NULL END DESC, CASE WHEN #{sort} = 4 THEN PRODUCT_PRICE ELSE NULL END ASC

SELECT COUNT(*)
FROM "PRODUCT" 
WHERE CATEGORY_NO = #{smallcategoryNo}
AND PRODUCT_UPDATE_DATE IS NOT NULL

	AND PRODUCT_PRICE > #{minPrice}
	AND PRODUCT_PRICE < #{maxPrice}

SELECT CATEGORY_NO, CATEGORY_NAME, b.BIG_CATEGORY_NO, BIG_CATEGORY_NAME
FROM CATEGORY c JOIN BIG_CATEGORY b ON(c.BIG_CATEGORY_NO = b.BIG_CATEGORY_NO)
WHERE b.BIG_CATEGORY_NO = #{bigcategoryNo}

SELECT BIG_CATEGORY_NAME
FROM BIG_CATEGORY
WHERE BIG_CATEGORY_NO = #{bigcategoryNo}

SELECT DISTINCT OPTION_NAME AS "optionName"
FROM "OPTION"
WHERE PRODUCT_NO = #{productNo}

SELECT p.PRODUCT_NO, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_CREATE_DATE, PRODUCT_UPDATE_DATE, PRODUCT_FL, p.CATEGORY_NO "CATEGORY_NO", PARTNER_NO, UPLOAD_IMG_NO, UPLOAD_IMG_PATH, UPLOAD_IMG_OG_NAME, UPLOAD_IMG_RENAME, BIG_CATEGORY_NO, UPLOAD_IMG_ORDER, CATEGORY, CASE WHEN EXISTS (SELECT 1 FROM WISHLIST w WHERE w.PRODUCT_NO = p.PRODUCT_NO AND w.MEMBER_NO = #{memberNo}) THEN 1 ELSE 0 END AS WISH FROM PRODUCT p JOIN UPLOAD_FILE uf ON(p.PRODUCT_NO = uf.PRODUCT_NO) LEFT JOIN CATEGORY c ON(p.CATEGORY_NO = c.CATEGORY_NO) NATURAL JOIN BIG_CATEGORY WHERE UPLOAD_IMG_ORDER = 0 AND PRODUCT_FL = 'N' AND (PRODUCT_NAME LIKE '%' || #{query} || '%' OR CATEGORY_NAME LIKE '%' || #{query} || '%' OR BIG_CATEGORY_NAME LIKE '%' || #{query} || '%') AND PRODUCT_UPDATE_DATE IS NOT NULL
AND PRODUCT_PRICE > #{minPrice} AND PRODUCT_PRICE < #{maxPrice}
ORDER BY PRODUCT_NO

SELECT *
FROM "MEMBER"
WHERE MEMBER_NO = #{memberNo}

SELECT REVIEW_NO
FROM REVIEW
WHERE ORDER_ITEM_NO = #{orderItemNo}

SELECT COUNT(*)
FROM REVIEW
WHERE PRODUCT_NO = #{productNo}

SELECT * 
FROM "ORDERS" 
WHERE MERCHANT_UID = #{merchantUid}
