mapper.select:
SELECT COUNT(*)
FROM "MEMBER"
WHERE MEMBER_NAME = #{memberName} 
AND MEMBER_EMAIL = #{memberEmail}
AND MEMBER_DEL_FL = 'N'
AND SOCIAL_LOGIN_TYPE = 'D'

SELECT MEMBER_ID, TO_CHAR(ENROLL_DATE, 'YY"."MM"."DD') ENROLL_DATE
FROM "MEMBER"
WHERE MEMBER_EMAIL = #{memberEmail}
AND MEMBER_NAME = #{memberName}
AND MEMBER_DEL_FL = 'N'
AND SOCIAL_LOGIN_TYPE = 'D'

SELECT MEMBER_ID, TO_CHAR(ENROLL_DATE, 'YY"."MM"."DD') ENROLL_DATE
FROM "MEMBER"
WHERE MEMBER_Tel = #{memberTel}
AND MEMBER_NAME = #{memberName}
AND MEMBER_DEL_FL = 'N'
AND SOCIAL_LOGIN_TYPE = 'D'

SELECT COUNT(*)
FROM "MEMBER"
WHERE MEMBER_NAME = #{memberName} 
AND MEMBER_TEL = #{memberTel}
AND MEMBER_DEL_FL = 'N'
AND SOCIAL_LOGIN_TYPE = 'D'
